import React, { useState } from 'react';

const App = () => {
  const [targetAmount, setTargetAmount] = useState('');
  const [coinDenominations, setCoinDenominations] = useState('');
  const [optimizedCoins, setOptimizedCoins] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      targetAmount: parseFloat(targetAmount),
      coinDenominations: coinDenominations.split(',').map(denomination => parseFloat(denomination.trim())),
    };

    try {
      const response = await fetch('http://localhost:8080/api/coins/optimize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      console.log('Response from server:', data); // Log the response for debugging

      // Update state to display the optimized coins
      setOptimizedCoins(data);

    } catch (error) {
      console.error('Error fetching data:', error);
      // Handle error (display error message, retry logic, etc.)
    }
  };

  return (
    <div>
      <h1>Coin Optimizer App</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Target Amount:
          <input
            type="text"
            value={targetAmount}
            onChange={(e) => setTargetAmount(e.target.value)}
          />
        </label>
        <br />
        <label>
          Coin Denominations (comma-separated):
          <input
            type="text"
            value={coinDenominations}
            onChange={(e) => setCoinDenominations(e.target.value)}
          />
        </label>
        <br />
        <button type="submit">Optimize Coins</button>
      </form>

      {/* Display optimized coins */}
      {optimizedCoins.length > 0 && (
        <div>
          <h2>Optimized Coins:</h2>
          <ul>
            {optimizedCoins.map((coin, index) => (
              <li key={index}>{coin}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
